@push('script')
<script>
    const prev = document.querySelector('#prev')
    const next = document.querySelector('#next')
    let count = document.querySelector('#count')
    const banners = document.querySelectorAll('.banner')
    const menu = document.querySelector('#menu')
    const offCanvas = document.querySelector('#off-canvas')
    const overlay = document.querySelector('#overlay')
    const close = document.querySelector('#close')
    let skip = 10;
    const take = 10;
    const dataContainer = document.querySelector('#data-container');
    const loadMoreButton = document.querySelector('#load-more-button');
    const loading = document.querySelector('#loading');


    // banner image preview button
    prev.onclick = () => {
        if (count.innerHTML == '1') {
            count.innerHTML = 5
            banners.forEach(banner => {
                banner.classList.add('animate-slide-effect')
                if (banner.classList.contains('desk')) {
                    banner.src = `/images/banner_desk/${count.innerHTML}.png`
                } else {
                    banner.src = `/images/banner_mobile/${count.innerHTML}.png`
                }
                setTimeout(() => {
                    banner.classList.remove('animate-slide-effect')
                }, 1000);
            })
            
        } else {
            let newCount = parseInt(count.innerHTML)
            newCount -= 1
            count.innerHTML = newCount
            banners.forEach(banner => {
                banner.classList.add('animate-slide-effect')
                if (banner.classList.contains('desk')) {
                    banner.src = `/images/banner_desk/${count.innerHTML}.png`
                } else {
                    banner.src = `/images/banner_mobile/${count.innerHTML}.png`
                }
                setTimeout(() => {
                    banner.classList.remove('animate-slide-effect')     
                }, 1000);
            })
        }
    }

    // banner image next button
    next.onclick = () => {
        if (count.innerHTML == '5') {
            count.innerHTML = 1
            banners.forEach(banner => {
                banner.classList.add('animate-slide-effect')
                if (banner.classList.contains('desk')) {
                    banner.src = `/images/banner_desk/${count.innerHTML}.png`
                } else {
                    banner.src = `/images/banner_mobile/${count.innerHTML}.png`
                }
                setTimeout(() => {
                    banner.classList.remove('animate-slide-effect')     
                }, 1000);
            })
        } else {
            let newCount = parseInt(count.innerHTML)
            newCount += 1
            count.innerHTML = newCount
            banners.forEach(banner => {
                banner.classList.add('animate-slide-effect')
                if (banner.classList.contains('desk')) {
                    banner.src = `/images/banner_desk/${count.innerHTML}.png`
                } else {
                    banner.src = `/images/banner_mobile/${count.innerHTML}.png`
                }
                setTimeout(() => {
                    banner.classList.remove('animate-slide-effect')     
                }, 1000);
            })
        }
    }

    // menu icon on click
    menu.onclick = (e) => {
        e.preventDefault();
        offCanvas.classList.remove('hidden')
        overlay.classList.remove('hidden')
    }

    // close icon on click
    close.onclick = (e) => {
        e.preventDefault();
        offCanvas.classList.add('hidden')
        overlay.classList.add('hidden')
    }

    // load more button
    loadMoreButton.addEventListener('click', () => {
        loadMoreButton.classList.add('hidden');
        loading.classList.remove('hidden');

        fetch(`/load-more?skip=${skip}`)
            .finally(() => {
                loadMoreButton.classList.remove('hidden');
                loading.classList.add('hidden');
            })
            .then(response => response.json())
            .then(data => {
                if (data.length > 0) {
                    data.forEach(record => {
                        
                        const cardD = `
                        <div class="aspect-square relative" data-aos="zoom-out">
                            <a href="/show/${record.slug}" class="group">
                                <img src="/storage/${record.photo}" class="w-full h-full object-cover">
                                <div class="absolute w-full h-full flex group-hover:bg-primary/70 duration-300 top-0 justify-center flex-col items-center">
                                    <span class="text-transparent text-center group-hover:text-white font-notoSerif">${record.name}</span>
                                    <span class="text-transparent group-hover:text-white text-xs font-notoSerif">${record.category.name}</span>
                                </div>
                            </a>
                        </div>
                        `;

                        const cardM = `
                        <div class="w-full h-[350px] relative" data-aos="zoom-out">
                            <a href="/show/${record.slug}" class="group">
                                <img src="/storage/${record.photo}" class="w-full h-full object-cover">
                                <div class="absolute w-full h-full group-hover:bg-primary/70 top-0 duration-300 flex flex-col justify-center items-center">
                                    <span class="text-transparent text-center group-hover:text-white font-notoSerif">${record.name}</span>
                                    <span class="text-transparent group-hover:text-white text-xs font-notoSerif">${record.category.name}</span>
                                </div>
                            </a>
                        </div>
                        `;

                        const wrapperM = document.querySelector('#productWrapM');
                        const wrapperD = document.querySelector('#productWrapD');

                        wrapperM.innerHTML += cardM;
                        wrapperD.innerHTML += cardD;
                    });

                    skip += take;
                } else {
                    loadMoreButton.style.display = 'none';
                }
            })
            .catch(error => {
                console.error(error);
            });
    });
    
</script>
@endpush